package JavaClass7;

public class HWQ4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//4. Print odd numbers between 20 and 50 (2 ways)
		
	      int c=21;

	        while (c<=50) {
	            System.out.print(c+ " "); //2
	            c +=2; // c=c+2=4
	        }
	        
	        System.out.println("-------------------------");
	        
	        int b=20; 
	        while(b<=50) {
	        	if(b%2==1) {
	        		System.out.print(b+ " ");
	        }
	        	b++;
	}
	
	}

}
