package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//storing and representing whole number values;
		byte box1 = 127;
		
		short box2=32767;
		
		int box3=100000; //popular
		
		long box4=5765869886976l; //credit cards, transaction;
			
		//storing and representing decimal values;
		float variable1=1.99F; 
		double variable2=99.99;//popular
		
		//storing single characters
		
		char container='A';
		char dollar='$'; 
		
		//storing yes or no values
		boolean hot=true;
		boolean tired=false;
		
		System.out.println(variable2);//printing value of the variable
		System.out.println("variable2"); //printing text variable
		
		System.out.println(tired);
		//System.outprintln(variable3); error - variable does not exist
	}

}
