package reviewclass1;

public class VariablesDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		/*
		 * float
		 * double // most widely used
		 */
		
		/*
		 *  float box1= 12.5f; //both lowercase and uppercase F works 
		 *  double box1=15.6; //wont work because it has the same name as above
		 */

		
		float box1=12.5F;
		double box2=15.6;
		
		box1=10; //re-assigning 10 to box1
	    box1=box1+5; // i am again changing the value of box1
		System.out.println(box1);
		System.out.println(box2);
		
	}

}
